import { Stack, Typography } from "@mui/material";
import { useLocation } from "react-router-dom";

const Sidebar = () => {
  const pages = [
    { number: 1, name: "KY Sheet Details" },
    { number: 2, name: "Hints" },
    { number: 3, name: "RISK AREAS" },
  ];

  const { pageNumber } = useLocation();
  return (
    <Stack spacing={3} justifyContent="center">
      {pages.map((page) => (
        <Stack key={page.number} direction="row" spacing={2}>
          <Typography
            textAlign="center"
            sx={{
              width: 20,
              height: 20,
              backgroundColor: pageNumber === page.number ? "#000080" : "",
              borderRadius: "50%",
              color: pageNumber === page.number ? "#fff" : "",
            }}
          >
            {page.number}
          </Typography>
          <Typography>{page.name}</Typography>
        </Stack>
      ))}
    </Stack>
  );
};
export default Sidebar;
