// eslint-disable-next-line
import React from "react";
import "./Index.css";
// import GirlImage from "../../assets/girl-image-laptop.png";
import ArrowIcon from "../../assets/white_arrow.png";
import RISK_ICON from '../../assets/risk.png'
 
const cardData = [
  {
    title: "KY Hazard Application",
    description: "Heavy Reconstruction Engine",
    linkText: "Explore our case studies",
    background:
      "https://f45training.com/wp-content/uploads/2023/07/img-2-4.png",
    icon: "hazard-icon.svg",
  },
  {
    title: "Work Permit Digitalisation",
    description: "Permit Digitization",
    linkText: "Explore our safety record",
    background:
      "https://f45training.com/wp-content/uploads/2023/07/img-2-4.png",
    icon: "permit-icon.svg",
  },
  {
    title: "Work Permit Digitalisation",
    description: "Permit Digitization",
    linkText: "Explore our safety record",
    background:
      "https://f45training.com/wp-content/uploads/2023/07/img-2-4.png",
    icon: "permit-icon.svg",
  },
];

function CardComponent() {
  return (
    <div className="card-container-home">
      {cardData.map((card, index) => (
        <div className="cardNew" key={index}>
          <div className="text-content">
            <h2>{card.title}</h2>
            <p>{card.description}</p>
            <div className="icon-text-container">
              <a href="#">{card.linkText}</a>


              <img className = "arrow-icon" height={"50px"} width={"30px"} src={ArrowIcon} alt="arrow-icon"></img>
            </div>
          </div>
          <div className="image">
            <img height = "100px" width = "100px" src={RISK_ICON} alt="risk"></img>
          </div>
        </div>
      ))}
    </div>
  );
}

export default CardComponent;
