// CardComponent.js
// eslint-disable-next-line
import React from 'react';
import './Index.css';

const CardComponent = () => {

    const cardData = [
        {
          heading: 'KY Hazard Application',
          subheading: 'Hazard Recommendation Engine',
          subtext: 'Card 1 Subtext',
          backgroundImage: 'background_image_1.jpg',
          logo: 'logo_1.png',
        },
        {
          heading: 'Card 2 Heading',
          subheading: 'Card 2 Subheading',
          subtext: 'Card 2 Subtext',
          backgroundImage: 'background_image_2.jpg',
          logo: 'logo_2.png',
        },
        {
          heading: 'Card 3 Heading',
          subheading: 'Card 3 Subheading',
          subtext: 'Card 3 Subtext',
          backgroundImage: 'background_image_3.jpg',
          logo: 'logo_3.png',
        },
      ];
    
  return (
    <div className="card-container">
      {cardData.map((card, index) => (
        <div className="card" key={index}>
          <div className="card-content">
            <h2 className='heading'>{card.heading}</h2>
            <h3>{card.subheading}</h3>
            <p>{card.subtext}</p>
          </div>
          <img src={card.backgroundImage} alt={`Card ${index + 1} Background`} className="card-image" />
          <img src={card.logo} alt={`Card ${index + 1} Logo`} className="card-logo" />
        </div>
      ))}
    </div>
  );
};

export default CardComponent;
