import { useState } from "react";
import {
  Checkbox,
  Container,
  FormControlLabel,
  FormGroup,
  Stack,
  Typography,
  Alert,
} from "@mui/material";

export default function KYsheetDetails() {
  const [selectedCheckboxes, setSelectedCheckboxes] = useState([]);
  const handleSubmit = (e) => {
    console.log(e.target.value);
  };

  const handleCheckboxChange = (event) => {
    const checkboxName = event.target.name;
    setSelectedCheckboxes((prevSelected) => {
      if (event.target.checked) {
        return [...prevSelected, checkboxName];
      } else {
        return prevSelected.filter((name) => name !== checkboxName);
      }
    });
  };
  const labels = ["first", "second", "third"];

  const MappedCheckBoxes = () => {
    return labels.map((label) => (
      <FormControlLabel
        key={label}
        sx={{ padding: 0 }}
        control={
          <Checkbox
            sx={{ paddingY: 0 }}
            name={label}
            onChange={handleCheckboxChange}
          />
        }
        label={label}
      />
    ));
  };
  console.log(selectedCheckboxes);

  return (
    <Container maxWidth="95%">
      <Stack spacing={2} maxWidth="300px">
        <Stack spacing={2} direction="row" alignItems="center">
          <Typography
            sx={{
              width: "20px",
              height: "20px",
              borderRadius: "50%",
              border: "2px solid blue",
              textAlign: "center",
            }}
          >
            1
          </Typography>
          <Typography sx={{ fontWeight: 700, fontSize: "14px" }}>
            KY Sheet Details
          </Typography>
        </Stack>
        <Typography sx={{ fontWeight: 700, fontSize: "10px" }}>
          Please Check if the following items is applicable
        </Typography>
        <Stack>
          <FormGroup onSubmit={handleSubmit} spacing={1}>
            <FormControlLabel
              sx={{ m: 0 }}
              control={
                <Checkbox
                  size="small"
                  sx={{ paddingY: 0 }}
                  name="Required"
                  onChange={handleCheckboxChange}
                />
              }
              label={
                <Typography sx={{ fontWeight: 700, fontSize: "10px" }}>
                  Power OFF
                </Typography>
              }
            />
            <FormControlLabel
              sx={{ m: 0 }}
              control={
                <Checkbox
                  size="small"
                  sx={{ paddingY: 0 }}
                  name="Required"
                  onChange={handleCheckboxChange}
                />
              }
              label={
                <Typography sx={{ fontWeight: 700, fontSize: "10px" }}>
                  Removal of safety key / plug
                </Typography>
              }
            />
            <FormControlLabel
              sx={{ m: 0 }}
              control={
                <Checkbox
                  size="small"
                  sx={{ paddingY: 0 }}
                  name="Required"
                  onChange={handleCheckboxChange}
                />
              }
              label={
                <Typography sx={{ fontWeight: 700, fontSize: "10px" }}>
                  Cover of hot working area of machine / location
                </Typography>
              }
            />
            <FormControlLabel
              sx={{ m: 0 }}
              control={
                <Checkbox
                  size="small"
                  sx={{ paddingY: 0 }}
                  name="Required"
                  onChange={handleCheckboxChange}
                />
              }
              label={
                <Typography sx={{ fontWeight: 700, fontSize: "10px" }}>
                  Pressure source turned OFF
                </Typography>
              }
            />
            <FormControlLabel
              sx={{ m: 0 }}
              control={
                <Checkbox
                  size="small"
                  sx={{ paddingY: 0 }}
                  name="Required"
                  onChange={handleCheckboxChange}
                />
              }
              label={
                <Typography sx={{ fontWeight: 700, fontSize: "10px" }}>
                  Release residual energy
                </Typography>
              }
            />
            <FormControlLabel
              sx={{ m: 0 }}
              control={
                <Checkbox
                  size="small"
                  sx={{ paddingY: 0 }}
                  name="Required"
                  onChange={handleCheckboxChange}
                />
              }
              label={
                <Typography sx={{ fontWeight: 700, fontSize: "10px" }}>
                  Barricade fall point
                </Typography>
              }
            />
            <FormControlLabel
              sx={{ m: 0 }}
              control={
                <Checkbox
                  size="small"
                  sx={{ paddingY: 0 }}
                  name="Required"
                  onChange={handleCheckboxChange}
                />
              }
              label={
                <Typography sx={{ fontWeight: 700, fontSize: "10px" }}>
                  Fuel stop
                </Typography>
              }
            />
            <FormControlLabel
              sx={{ m: 0 }}
              control={
                <Checkbox
                  size="small"
                  sx={{ paddingY: 0 }}
                  name="Required"
                  onChange={handleCheckboxChange}
                />
              }
              label={
                <Typography sx={{ fontWeight: 700, fontSize: "10px" }}>
                  Remove remaining fuel
                </Typography>
              }
            />
            <FormControlLabel
              sx={{ m: 0 }}
              control={<Checkbox size="small" sx={{ paddingY: 0 }} />}
              label={
                <Typography sx={{ fontWeight: 700, fontSize: "10px" }}>
                  Display Under Work board / Under Maintenance
                </Typography>
              }
            />
          </FormGroup>
        </Stack>
        <Alert
          severity="warning"
          sx={{
            border: "1px solid red",
            width: "400px",
            height: "160px",
            backgroundColor: "",
          }}
        >
          <ol>
            <li>
              <Typography sx={{ fontSize: "12px" }}>
                Power for electrical portable equipment shall be taken from
                industrial socket board of appropriate rating
              </Typography>
            </li>
            <li>
              <Typography sx={{ fontSize: "12px" }}>
                No portable equlpment shall be connected through IT Power
                sockets / sockets of alectrical panel
              </Typography>
            </li>
            <li>
              <Typography sx={{ fontSize: "12px" }}>
                Obtaln approval from concern department for power connection
                from PDB
              </Typography>
            </li>
          </ol>
        </Alert>
      </Stack>
    </Container>
  );
}
